    }

    render()
    {
        return(
            <div className="dynamic_course_div">
                <h2> Courses</h2>
                <ul className='course_list'>
                    <CourseFeedSelectorItem
                    courseNameList={this.state.courseList}
                    courseIdList={this.state.courseList}
                    />
                </ul>
            </div>
        );
    }
}



export default CourseFeedSelector;
